import React, { Component } from 'react'
import { connect } from 'react-redux'

class SanPhamRedux extends Component {


    render() {
        const { sanPham } = this.props
        return (
            <div className="card text-white bg-primary">
                <img className="card-img-top" src={sanPham.hinhAnh} width={150} height={250} alt={sanPham.hinhAnh} />
                <div className="card-body">
                    <h4 className="card-title">{sanPham.tenSP}</h4>
                    <p className="card-text">{sanPham.giaBan}</p>
                    <button onClick = {()=> t}></button>
                </div>
            </div>


        )
    }
}

// Xây dựng hàm tạo ra props là hàm xử lý sự kiện để đưa dữ liệu lên store
const mapDispatchToProps = (dispatch) => {
    return {
        // tạo ra props component(là function => đưa dữ liệu lên store)
        themGioHang: (sanPham) => {
            const spGioHang = {
                maSP: sanPham.maSP,
                tenSP: sanPham.tenSP,
                hinhAnh: sanPham.hinhAnh,
                soLuong: 1,
                giaBan: sanPham.giaBan,

            }
            // tạo action đưa dữ liệu lên reducer
            const action = {
                type: 'THEM_GIO_HANG', // bắt buộc
                spGioHang: spGioHang // nội dung gửi lên reducer

            }
            // dùng hàm dispatch để đưa dữ liệu action lên reducer
            dispatch(action);
        }
    }
}
export default connect(null, mapDispatchToProps)(SanPhamRedux)
