}

export const QuanLySinhVienReducer = (state = stateDefault,action) => {
    switch (action.type) {
        case 'THEM_SINH_VIEN':{
            console.log(action);
            const {sinhVien} = action
            const mangSinhVienUpdate = [...state.mangSinhVien, sinhVien];
           state.mangSinhVien = mangSinhVienUpdate
          
            return {...state}
        };break;
            
           
    
        default:
            return {...state}
    }
   
}
