import React, { Component } from "react";
import { connect } from "react-redux";
class FormSinhVien extends Component {
  state = {
    values: {
      maSV: "",
      tenSV: "",
      sdt: "",
      email: ""
    },
    errors: {
      maSV: "",
      tenSV: "",
      sdt: "",
      email: ""
    }
  };

  handleOnchange = e => {
    // lấy giá trị mỗi lận value input thay đổi bởi người dùng

    let tagInput = e.target;
    let { name, value } = tagInput;
    this.setState(
      {
        [name]: value
      },
      () => {
        console.log(this.state);
      }
    );
  };

  handleSubmit = e => {
    e.preventDefault(); // cản sự kiện load lại trang của browser
    this.props.themSinhVien(this.state);
  };
  render() {
    return (
      <div className="container">
        <div className="card text-left">
          <div className="card-header bg-dark text-center text-white">
            <h3>Thông tin sinh viên</h3>
          </div>
          <div className="card-body">
            <form onSubmit={this.handleSubmit}>
              <div className="row">
                <div className="form-group col-6">
                  <label>Mã sinh viên</label>
                  <input
                    className="form-control"
                    name="maSV"
                    value={this.state.values.maSV}
                    onChange={this.handleOnchange}
                  ></input>
                  <p>{this.state.errors.maSV}</p>
                </div>
                <div className="form-group col-6">
                  <label>Tên sinh viên</label>
                  <input
                    className="form-control"
                    name="tenSV"
                    value={this.state.values.tenSV}
                    onChange={this.handleOnchange}
                  ></input>
                </div>
              </div>
              <div className="row">
                <div className="form-group col-6">
                  <label>Số điện thoại</label>
                  <input
                    className="form-control"
                    name="sdt"
                    value={this.state.values.sdt}
                    onChange={this.handleOnchange}
                  ></input>
                </div>
                <div className="form-group col-6">
                  <label>Email</label>
                  <input
                    className="form-control"
                    name="email"
                    value={this.state.values.email}
                    onChange={this.handleOnchange}
                  ></input>
                </div>
              </div>
              <div className="row">
                <div className="col-12 text-right">
                  <button type="submit" className="btn btn-danger ">
                    Thêm sinh viên
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
const mapDispatchToProps = dispatch => {
  return {
    themSinhVien: sinhVien => {
      const action = {
        type: "THEM_SINH_VIEN",
        sinhVien
      };
      dispatch(action);
    }
  };
};
export default connect(null, mapDispatchToProps)(FormSinhVien);
