        //this.props.gioHang là thuộc tính nhận từ redux
        console.log(this.props.gioHang);

        return (
            <div className='container'>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>mã SP</th>
                            <th>tên SP</th>
