import React, { Component } from 'react'
import { connect } from "react-redux";
class FormSinhVien extends Component {
    state = {
       values:{
        maSV: '' ,
        hoTen: '',
        email: '',
        sdt: ''
       },
       errors: {
           
       }
    }
    handleChange = (e) => {
        let tagInput = e.target;
      let {name,value} = tagInput
      this.setState({
          [name]: value
      }, ()=> {
          console.log(this.state);
          
      })
    }
    handleSubmit = (e) =>{
        e.preventDefault(); // cản sự kiện load lại trang
        this.props.themSinhVien(this.state)
    }   
    render() {
        return (
            <div className='container'>
                <h3 className='bg-dark text-white text-center'>Thông tin sinh viên</h3>
               <form onSubmit = {this.handleSubmit} >
               <div className='row'>
                <div className='col-6'>
                <div className="form-group">
                    <label >Mã SV</label>
                    <input type="text" className="form-control" name ='maSV' value = {this.state.maSV}  onChange ={this.handleChange} placeholder />
                  
                </div>
                <div className="form-group">
                    <label >Số điện thoại</label>
                    <input type="text" className="form-control" name = 'sdt' value = {this.state.sdt} onChange ={this.handleChange} placeholder />
                   
                </div>
                </div>
                <div className='col-6'>
                <div className="form-group">
                    <label >Họ tên</label>
                    <input type="text" className="form-control" name ='hoTen' value = {this.state.hoTen} onChange ={this.handleChange} placeholder />
                  
                </div>
                <div className="form-group">
                    <label >Email</label>
                    <input type="text" className="form-control" name ='email' value = {this.state.email} onChange ={this.handleChange}  placeholder />
                  
                </div>
                </div>
               </div>

                <div className ='text-right'><button type='submit' className='btn btn-success'>Submit</button></div>
               </form>
            </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        themSinhVien : (sinhVien) => {
            const action = {
                type: 'THEM_SINH_VIEN',
                sinhVien 
            }
            dispatch(action)
        }
    }
}

export default connect(null,mapDispatchToProps)(FormSinhVien)
