import React, { Component } from "react";

class FormSinhVien extends Component {
  render() {
    return (
      <div className="container">
        <div className="card text-left">
          <div className="card-header bg-dark text-center text-white">
            <h3>Thông tin sinh viên</h3>
          </div>
          <div className="card-body">
            <form>
              <div className="row">
                <div className="form-group col-6">
                  <label>Mã sinh viên</label>
                  <input className="form-control" name="maSV"></input>
                </div>
                <div className="form-group col-6">
                  <label>Tên sinh viên</label>
                  <input className="form-control" name="tenSV"></input>
                </div>
              </div>
              <div className="row">
                <div className="form-group col-6">
                  <label>Số điện thoại</label>
                  <input className="form-control" name="sdt"></input>
                </div>
                <div className="form-group col-6">
                  <label>Email</label>
                  <input className="form-control" name="email"></input>
                </div>
              </div>
              <button className=''>Thêm sinh viên</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default FormSinhVien;
