const stateDefault = {
    mangSinhVien : [{maSV:1, hoTen: 'Lucas Moura', sdt:'0909991995', email: 'abc@gmail.com'}],
}

export const QuanLySinhVienReducer = (state = stateDefault,action) => {
    switch (action.type) {
        case 'THEM_SINH_VIEN': {
            let mangSVUpdate = [...state.mangSinhVien];
           state.mangSinhVien = mangSVUpdate
           return {...state}
            
        }break;
            
       
    
        default:{
            return {...state}
        } 
           
    }
    return {...state}
    

}
